mapper.select:
select m.meeting_id, mec.code, date_format(mec.last_modified_date, '%Y-%m-%d %H:%i:%S') as 'last_modified_date', mm.user_id
from meeting m
         left join meeting_entry_code mec on m.meeting_id = mec.meeting_id
         left join meeting_member mm on m.meeting_id = mm.meeting_id and mm.user_id = #{userId}
where m.meeting_id = #{meetingId};
